// SPDX-License-Identifier: AGPL-3.0
pragma solidity 0.8.4;

// contracts
import {BaseERC20} from "./BaseERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// interfaces
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

// libraries
import {LibMath} from "../lib/LibMath.sol";
import {LibReserve} from "../lib/LibReserve.sol";

/// @notice Unit of Account (UA) is a USDC-backed token used as the unit of account accross Increment
contract UA is BaseERC20 {
    using SafeERC20 for IERC20Metadata;
    using LibMath for int256;
    using LibMath for uint256;

    /// @notice Emitted when the proposed insurance address is equal to the zero address
    error UA_ReserveTokenZeroAddress();

    IERC20Metadata public immutable reserveToken;

    constructor(address _reserveToken) BaseERC20("Increment Unit of Account", "UA") {
        if (address(_reserveToken) == address(0)) revert UA_ReserveTokenZeroAddress();
        reserveToken = IERC20Metadata(_reserveToken);
    }

    /// @notice Mint UA with USDC, 1:1 backed
    /// @param amount Amount of reserve token, i.e. USDC amount. 6 decimals
    function mintWithReserve(uint256 amount) external {
        reserveToken.safeTransferFrom(msg.sender, address(this), amount);

        uint256 wadAmount = LibReserve.tokenToWad(reserveToken.decimals(), amount.toInt256()).toUint256();
        _mint(msg.sender, wadAmount);
    }

    /// @notice Burn UA in exchange of USDC
    /// @param amount UA amount. 18 decimals
    function withdraw(uint256 amount) external {
        _burn(msg.sender, amount);

        uint256 tokenAmount = LibReserve.wadToToken(reserveToken.decimals(), amount.toInt256()).toUint256();
        reserveToken.safeTransfer(msg.sender, tokenAmount);
    }
}

contracts/tokens/BaseERC20.sol.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity 0.8.4;

// interfaces
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/// @notice Modern and gas efficient ERC20 implementation.
/// @author Modified from Solmate (https://github.com/Rari-Capital/solmate/blob/main/src/tokens/ERC20.sol)
/// @dev Do not manually set balances without updating totalSupply, as the sum of all user balances must not exceed it.
abstract contract BaseERC20 is IERC20, IERC20Metadata {
    /*///////////////////////////////////////////////////////////////
                             METADATA STORAGE
    //////////////////////////////////////////////////////////////*/

    string public override name;

    string public override symbol;

    uint8 public constant override decimals = 18;

    /*///////////////////////////////////////////////////////////////
                              ERC20 STORAGE
    //////////////////////////////////////////////////////////////*/

    uint256 public override totalSupply;

    mapping(address => uint256) public override balanceOf;

    mapping(address => mapping(address => uint256)) public override allowance;

    /*///////////////////////////////////////////////////////////////
                               CONSTRUCTOR
    //////////////////////////////////////////////////////////////*/

    constructor(string memory _name, string memory _symbol) {
        name = _name;
        symbol = _symbol;
    }

    /*///////////////////////////////////////////////////////////////
                              ERC20 LOGIC
    //////////////////////////////////////////////////////////////*/

    function approve(address spender, uint256 amount) external virtual override returns (bool) {
        allowance[msg.sender][spender] = amount;

        emit Approval(msg.sender, spender, amount);

        return true;
    }

    function transfer(address to, uint256 amount) external virtual override returns (bool) {
        balanceOf[msg.sender] -= amount;

        // Cannot overflow because the sum of all user
        // balances can't exceed the max uint256 value.
        unchecked {
            balanceOf[to] += amount;
        }

        emit Transfer(msg.sender, to, amount);

        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        uint256 allowed = allowance[from][msg.sender]; // Saves gas for limited approvals.

        if (allowed != type(uint256).max) allowance[from][msg.sender] = allowed - amount;

        balanceOf[from] -= amount;

        // Cannot overflow because the sum of all user
        // balances can't exceed the max uint256 value.
        unchecked {
            balanceOf[to] += amount;
        }

        emit Transfer(from, to, amount);

        return true;
    }

    /*///////////////////////////////////////////////////////////////
                       INTERNAL MINT/BURN LOGIC
    //////////////////////////////////////////////////////////////*/

    function _mint(address to, uint256 amount) internal virtual {
        totalSupply += amount;

        // Cannot overflow because the sum of all user
        // balances can't exceed the max uint256 value.
        unchecked {
            balanceOf[to] += amount;
        }

        emit Transfer(address(0), to, amount);
    }

    function _burn(address from, uint256 amount) internal virtual {
        balanceOf[from] -= amount;

        // Cannot underflow because a user's balance
        // will never be larger than the total supply.
        unchecked {
            totalSupply -= amount;
        }

        emit Transfer(from, address(0), amount);
    }
}

contracts/Vault.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity 0.8.4;

// contracts
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IncreOwnable} from "./utils/IncreOwnable.sol";

// interfaces
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {IInsurance} from "./interfaces/IInsurance.sol";
import {IOracle} from "./interfaces/IOracle.sol";
import {IVault} from "./interfaces/IVault.sol";
import {IClearingHouse} from "./interfaces/IClearingHouse.sol";

// libraries
import {LibReserve} from "./lib/LibReserve.sol";
import {LibMath} from "./lib/LibMath.sol";

/// @notice Keeps track of all token reserves for all market
contract Vault is IVault, IncreOwnable {
    using SafeERC20 for IERC20Metadata;
    using LibMath for uint256;
    using LibMath for int256;

    // constants
    IERC20Metadata public immutable UA;
    uint256 public constant UA_IDX = 0;

    // parameterization
    uint256 internal constant LIQUIDATION_DISCOUNT = 95e16; // TODO: make parametrize

    // dependencies
    IClearingHouse public override clearingHouse;
    IInsurance public override insurance;
    IOracle public override oracle;

    // global state
    uint256 internal traderBadDebt;
    Collateral[] public whiteListedCollaterals;
    /// @notice Map white listed collateral tokens to their whiteListedCollaterals indexes
    mapping(IERC20Metadata => uint256) public tokenToCollateralIdx;

    // user state

    /* Balances of users and liquidity providers
    We follow a strict separate margin design where have to deposit collateral for any market

    */
    //      trader  =>       marketIdx =>   collateralIdx => balance (might not be 18 decimals)
    mapping(address => mapping(uint256 => mapping(uint256 => int256))) private traderBalances;
    //      lp      =>       marketIdx =>   collateralIdx => balance (might not be 18 decimals)
    mapping(address => mapping(uint256 => mapping(uint256 => int256))) private lpBalances;

    constructor(IERC20Metadata _ua) {
        if (address(_ua) == address(0)) revert Vault_ZeroAddressConstructor(0);

        UA = _ua;
        addWhiteListedCollateral(_ua, 1e18);
    }

    modifier onlyClearingHouse() {
        if (msg.sender != address(clearingHouse)) revert Vault_SenderNotClearingHouse();
        _;
    }

    /* ****************** */
    /*     User flow      */
    /* ****************** */

    /// @notice Deposit reserveTokens to account
    /// @dev No differentiated treatment of deposit in the UA token and other collaterals
    /// @param marketIdx Index of the perpetual market
    /// @param user Account to deposit to
    /// @param amount Amount to be used as the collateral of the position. Might not be 18 decimals
    /// @param depositToken Token to be used for the collateral of the position
    /// @param isTrader True if the user is a trader, False if the user is a liquidity provider
    /// @return amountUSDValue USD value of the deposited amount because pools' vQuote are USD-based
    function deposit(
        uint256 marketIdx,
        address user,
        uint256 amount,
        IERC20Metadata depositToken,
        bool isTrader
    ) external override onlyClearingHouse returns (int256 amountUSDValue) {
        uint256 tokenIdx = tokenToCollateralIdx[depositToken];
        if (!((tokenIdx != 0) || (address(depositToken) == address(UA)))) revert Vault_UnsupportedCollateral();

        _changeBalance(user, marketIdx, tokenIdx, amount.toInt256(), isTrader);

        IERC20Metadata(depositToken).safeTransferFrom(user, address(this), amount);

        emit Deposit(marketIdx, user, address(depositToken), amount);

        return _getCollateralUSDValue(whiteListedCollaterals[tokenIdx], amount.toInt256());
    }

    /// @notice Withdraw all tokens from account
    /// @param marketIdx Index of the perpetual market
    /// @param user Account to withdraw from
    /// @param withdrawToken Token to be withdrawn from the vault
    /// @param isTrader True if the user is a trader, False if the user is a liquidity provider
    function withdrawAll(
        uint256 marketIdx,
        address user,
        IERC20Metadata withdrawToken,
        bool isTrader
    ) external override onlyClearingHouse {
        uint256 tokenIdx = tokenToCollateralIdx[withdrawToken];
        if (!((tokenIdx != 0) || (address(withdrawToken) == address(UA)))) revert Vault_UnsupportedCollateral();

        int256 fullAmount = isTrader
            ? traderBalances[user][marketIdx][tokenIdx]
            : lpBalances[user][marketIdx][tokenIdx];

        return withdraw(marketIdx, user, fullAmount.toUint256(), withdrawToken, isTrader);
    }

    /// @notice Withdraw share of tokens from account, across multi-collaterals
    /// @param marketIdx Index of the perpetual market
    /// @param user Account to withdraw from
    /// @param reductionRatio Share of collateral to be withdrawn. Min: 0. Max: 1e18
    /// @param isTrader True if the user is a trader, False if the user is a liquidity provider
    function withdrawPartial(
        uint256 marketIdx,
        address user,
        uint256 reductionRatio,
        bool isTrader
    ) external override onlyClearingHouse {
        if (reductionRatio > 1e18) revert Vault_WithdrawReductionRatioTooHigh();

        // the amount to withdraw accross all collateral
        int256 reserveValue = _getUserReserveValue(marketIdx, user, isTrader);
        int256 amountToWithdraw = reserveValue.wadMul(reductionRatio.toInt256());

        Collateral[] memory collaterals = whiteListedCollaterals;
        int256 collateralBalance;
        int256 wadCollateralBalance;
        int256 tokenAmountToWithdraw;

        for (uint256 i = collaterals.length; i > 0; i--) {
            collateralBalance = isTrader ? traderBalances[user][marketIdx][i - 1] : lpBalances[user][marketIdx][i - 1];
            wadCollateralBalance = LibReserve.tokenToWad(collaterals[i - 1].decimals, collateralBalance);

            if (wadCollateralBalance >= amountToWithdraw) {
                tokenAmountToWithdraw = LibReserve.wadToToken(collaterals[i - 1].decimals, amountToWithdraw);

                withdraw(marketIdx, user, tokenAmountToWithdraw.toUint256(), collaterals[i - 1].asset, isTrader);
                break;
            } else {
                withdraw(marketIdx, user, collateralBalance.toUint256(), collaterals[i - 1].asset, isTrader);
                amountToWithdraw -= wadCollateralBalance;
            }
        }
    }

    /// @notice Withdraw tokens from account
    /// @dev Unlike `deposit`, differentiated treatment of withdrawals in the UA token and other collaterals
    /// @param marketIdx Index of the perpetual market
    /// @param user Account to withdraw from
    /// @param amount Amount to withdraw from the vault. Might not be 18 decimals (decimals of the token)
    /// @param withdrawToken Token to be withdrawn from the vault
    /// @param isTrader True if the user is a trader, False if the user is a liquidity provider
    function withdraw(
        uint256 marketIdx,
        address user,
        uint256 amount,
        IERC20Metadata withdrawToken,
        bool isTrader
    ) public override onlyClearingHouse {
        uint256 tokenIdx = tokenToCollateralIdx[withdrawToken];
        if (!((tokenIdx != 0) || (address(withdrawToken) == address(UA)))) revert Vault_UnsupportedCollateral();

        // user can't withdraw his collateral with a UA debt
        int256 uaBalance = isTrader ? traderBalances[user][marketIdx][UA_IDX] : lpBalances[user][marketIdx][UA_IDX];
        if (uaBalance < 0) revert Vault_UADebt();

        // user can't withdraw more than his collateral balance
        int256 collateralBalance = isTrader
            ? traderBalances[user][marketIdx][tokenIdx]
            : lpBalances[user][marketIdx][tokenIdx];
        if (amount.toInt256() > collateralBalance) revert Vault_WithdrawExcessiveAmount();

        _changeBalance(user, marketIdx, tokenIdx, -amount.toInt256(), isTrader);

        if (amount > IERC20Metadata(withdrawToken).balanceOf(address(this))) revert Vault_InsufficientBalance();

        // transfer funds to user, whatever the collateral used
        emit Withdraw(marketIdx, user, address(withdrawToken), amount);

        IERC20Metadata(withdrawToken).safeTransfer(user, amount);
    }

    /// @notice Allow liquidator to buy back collateral(s) of liquidated user at discounted price whereby settling his debt
    /// @param liquidatee Liquidatee
    /// @param marketIdx Market on which the liquidation has to occur
    /// @param isTrader True if the user is a trader, False if the user is a liquidity provider
    function settleLiquidationOnCollaterals(
        address liquidator,
        address liquidatee,
        uint256 marketIdx,
        bool isTrader
    ) external override onlyClearingHouse {
        _settleLiquidationOnCollaterals(liquidator, liquidatee, marketIdx, isTrader);
    }

    /// @notice Withdraw tokens from account
    /// @param marketIdx Index of the perpetual market
    /// @param user Account to apply the PnL to
    /// @param amount PnL amount in UA to apply. 18 decimals
    /// @param isTrader True if the user is a trader, False if the user is a liquidity provider
    function settlePnL(
        uint256 marketIdx,
        address user,
        int256 amount,
        bool isTrader
    ) external override onlyClearingHouse {
        _changeBalance(user, marketIdx, UA_IDX, amount, isTrader);
    }

    /// @notice Transfer UA tokens from the vault
    /// @param user Account to withdraw UA tokens to
    /// @param amount Amount of UA tokens to be withdrawn. 18 decimals
    function transferUa(address user, uint256 amount) external override onlyClearingHouse {
        UA.safeTransfer(user, amount);
    }

    /* ****************** */
    /*     Governance     */
    /* ****************** */

    function setClearingHouse(IClearingHouse newClearingHouse) external onlyOwner {
        if (address(newClearingHouse) == address(0)) revert Vault_ClearingHouseZeroAddress();
        clearingHouse = newClearingHouse;
        emit ClearingHouseChanged(newClearingHouse);
    }

    function setInsurance(IInsurance newInsurance) external onlyOwner {
        if (address(newInsurance) == address(0)) revert Vault_InsuranceZeroAddress();
        insurance = newInsurance;
        emit InsuranceChanged(newInsurance);
    }

    function setOracle(IOracle newOracle) external onlyOwner {
        if (address(newOracle) == address(0)) revert Vault_OracleZeroAddress();
        oracle = newOracle;
        emit OracleChanged(newOracle);
    }

    /// @notice Add a new token to the list of white listed ERC20 which can be used as collaterals
    function addWhiteListedCollateral(IERC20Metadata asset, uint256 weight) public onlyOwner {
        if (weight < 1e16) revert Vault_InsufficientCollateralWeight();
        if (weight > 1e18) revert Vault_ExcessiveCollateralWeight();

        for (uint256 i = 0; i < whiteListedCollaterals.length; i++) {
            if (whiteListedCollaterals[i].asset == asset) revert Vault_CollateralAlreadyWhiteListed();
        }

        whiteListedCollaterals.push(Collateral({asset: asset, weight: weight, decimals: asset.decimals()}));
        tokenToCollateralIdx[asset] = whiteListedCollaterals.length - 1;

        emit CollateralAdded(asset, weight);
    }

    /// @notice Change weight of a white listed collateral
    ///         Useful as a risk mitigation measure in case one collateral drops in value
    function changeCollateralWeight(IERC20Metadata asset, uint256 newWeight) external onlyOwner {
        uint256 tokenIdx = tokenToCollateralIdx[asset];
        if (!((tokenIdx != 0) || (address(asset) == address(UA)))) revert Vault_UnsupportedCollateral();

        if (newWeight < 1e16) revert Vault_InsufficientCollateralWeight();
        if (newWeight > 1e18) revert Vault_ExcessiveCollateralWeight();

        for (uint256 i = 0; i < whiteListedCollaterals.length; i++) {
            if (whiteListedCollaterals[i].asset == asset) {
                whiteListedCollaterals[i].weight = newWeight;
            }
        }

        emit CollateralWeightChanged(asset, newWeight);
    }

    /* ****************** */
    /*   User getter      */
    /* ****************** */

    /// @notice Get the balance of a trader, accounted for in USD. 18 decimals
    /// @param marketIdx Perpetual market index
    /// @param trader Trader address
    /// @return reserveValue Value of collaterals in USD. 18 decimals
    function getTraderReserveValue(uint256 marketIdx, address trader) external view override returns (int256) {
        return _getUserReserveValue(marketIdx, trader, true);
    }

    /// @notice Get the balance of a liquidity provider, accounted for in USD. 18 decimals
    /// @param marketIdx Perpetual market index
    /// @param lp LP address
    /// @return reserveValue Value of collaterals in USD. 18 decimals
    function getLpReserveValue(uint256 marketIdx, address lp) external view override returns (int256) {
        return _getUserReserveValue(marketIdx, lp, false);
    }

    function getTraderBalance(
        address user,
        uint256 marketIdx,
        uint256 tokenIdx
    ) external view override returns (int256) {
        return traderBalances[user][marketIdx][tokenIdx];
    }

    function getLpBalance(
        address user,
        uint256 marketIdx,
        uint256 tokenIdx
    ) external view override returns (int256) {
        return lpBalances[user][marketIdx][tokenIdx];
    }

    /* ****************** */
    /*   Global getter    */
    /* ****************** */

    /// @notice Get the amount of UA tokens borrowed by insurance (bad debt)
    /// @return Amount of UA tokens borrowed by insurance. 18 decimals
    function getBadDebt() external view override returns (uint256) {
        return traderBadDebt;
    }

    /// @notice Get all value of all tokens deposited into the vault
    /// @return Total Value Locked (tvl) in USD. 18 decimals
    function getTotalValueLocked() external view override returns (uint256) {
        Collateral[] memory collaterals = whiteListedCollaterals;
        uint256 tvl = 0;

        for (uint256 i = 0; i < collaterals.length; i++) {
            int256 collateralBalance = collaterals[i].asset.balanceOf(address(this)).toInt256();

            if (collateralBalance > 0) {
                tvl += _getUAUndiscountedCollateralPrice(collaterals[i], collateralBalance);
            }
        }

        return tvl;
    }

    /// @notice Get all collateral tokens available for margin trading
    /// @return List of Collateral tokens
    function getWhiteListedCollaterals() external view override returns (Collateral[] memory) {
        return whiteListedCollaterals;
    }

    /* ****************** */
    /*   Internal Fcts    */
    /* ****************** */

    function _settleLiquidationOnCollaterals(
        address liquidator,
        address liquidatee,
        uint256 marketIdx,
        bool isTrader
    ) internal {
        int256 balance = isTrader
            ? traderBalances[liquidatee][marketIdx][UA_IDX]
            : lpBalances[liquidatee][marketIdx][UA_IDX];

        uint256 debtSize = balance < 0 ? (-balance).toUint256() : 0;

        if (debtSize == 0) return;

        Collateral[] memory collaterals = whiteListedCollaterals;
        int256 collateralBalance;

        for (uint256 i = 0; i < collaterals.length; i++) {
            collateralBalance = isTrader
                ? traderBalances[liquidatee][marketIdx][i]
                : lpBalances[liquidatee][marketIdx][i];

            if (collateralBalance > 0) {
                uint256 collateralUAValue = _getUAUndiscountedCollateralPrice(collaterals[i], collateralBalance);

                if (collateralUAValue < debtSize) {
                    // sell 100% of the collateral
                    debtSize -= _sellCollateral(
                        liquidator,
                        liquidatee,
                        marketIdx,
                        collaterals[i],
                        collateralBalance,
                        1e18,
                        isTrader
                    );
                } else {
                    // sell only what is needed of the collateral to cover debtSize
                    uint256 collateralSellRatio = debtSize.wadDiv(collateralUAValue);
                    _sellCollateral(
                        liquidator,
                        liquidatee,
                        marketIdx,
                        collaterals[i],
                        collateralBalance,
                        collateralSellRatio,
                        isTrader
                    );
                    debtSize = 0;
                    break;
                }
            }
        }
        // liquidator can't buy back all the debt
        // use funds from insurance if UA amount is insufficient
        if (debtSize > 0) {
            if (UA.balanceOf(address(this)) < debtSize) {
                insurance.settleDebt(debtSize);
            }

            traderBadDebt += debtSize;
            emit TraderBadDebtGenerated(marketIdx, liquidatee, debtSize);
        }
    }

    function _sellCollateral(
        address liquidator,
        address liquidatee,
        uint256 marketIdx,
        Collateral memory collateral,
        int256 collateralBalance,
        uint256 collateralSellRatio, // max 1e18
        bool isTrader
    ) internal returns (uint256) {
        uint256 collateralToSell = (collateralBalance.wadMul(collateralSellRatio.toInt256())).toUint256();

        // apply a discount to the collateral UA price & harmonize decimals to 18
        uint256 wadCollateralToSell = (LibReserve.tokenToWad(collateral.decimals, collateralToSell.toInt256()))
            .toUint256();
        uint256 discountedUAPriceOfCollateral = wadCollateralToSell.wadMul(LIQUIDATION_DISCOUNT);

        // liquidatee receives a discounted value of his collateral in UA
        _changeBalance(liquidatee, marketIdx, UA_IDX, discountedUAPriceOfCollateral.toInt256(), isTrader);
        IERC20Metadata(UA).safeTransferFrom(liquidator, address(this), discountedUAPriceOfCollateral);

        // liquidator receives the real value of the collateral
        _changeBalance(
            liquidator,
            marketIdx,
            tokenToCollateralIdx[collateral.asset],
            collateralToSell.toInt256(),
            true
        );

        return discountedUAPriceOfCollateral;
    }

    /// @notice Evaluate the Y USD price at which to sell X quantity of a given collateral
    /// @dev As a reminder, UA value = USD value
    /// @dev Same as _getCollateralUSDValue, expect that without weight
    function _getUAUndiscountedCollateralPrice(Collateral memory collateral, int256 collateralBalance)
        internal
        view
        returns (uint256)
    {
        int256 wadCollateralBalance = LibReserve.tokenToWad(collateral.decimals, collateralBalance); // 18 decimals

        int256 pricePerUnit = oracle.getPrice(address(collateral.asset)); // 18 decimals
        return wadCollateralBalance.wadMul(pricePerUnit).toUint256();
    }

    function _changeBalance(
        address user,
        uint256 marketIdx,
        uint256 tokenIdx,
        int256 amount,
        bool isTrader
    ) internal {
        if (isTrader) {
            traderBalances[user][marketIdx][tokenIdx] += amount;
        } else {
            lpBalances[user][marketIdx][tokenIdx] += amount;
        }
    }

    /// @notice Get the balance of a trader, accounted for in USD. 18 decimals
    /// @param marketIdx Perpetual market index
    /// @param user User address
    /// @return reserveValue Value of collaterals in USD. 18 decimals
    function _getUserReserveValue(
        uint256 marketIdx,
        address user,
        bool isTrader
    ) internal view returns (int256) {
        Collateral[] memory collaterals = whiteListedCollaterals;
        int256 collateralBalance;

        int256 reserveValue = 0;
        for (uint256 i = 0; i < collaterals.length; i++) {
            collateralBalance = isTrader ? traderBalances[user][marketIdx][i] : lpBalances[user][marketIdx][i];

            // user might have a negative UA balance
            if (collateralBalance != 0) {
                reserveValue += _getCollateralUSDValue(collaterals[i], collateralBalance);
            }
        }

        return reserveValue;
    }

    /// @notice Return collateral value in USD, normalized to 18 decimals
    /// @param collateral Collateral to evaluate
    /// @param collateralBalance Balance in the collateral. Might not be 18 decimals (decimals of the collateral token)
    /// @return collateralUSDValue Value of the collateral in USD, harmonized to 18 decimals
    function _getCollateralUSDValue(Collateral memory collateral, int256 collateralBalance)
        internal
        view
        returns (int256)
    {
        // collateralUSDValue = collateralBalance * weight * oracleUSDPrice
        int256 wadCollateralBalance = LibReserve.tokenToWad(collateral.decimals, collateralBalance); // 18 decimals
        int256 weightedWadCollateralBalance = wadCollateralBalance.wadMul(collateral.weight.toInt256()); // 18 decimals

        int256 pricePerUnit = oracle.getPrice(address(collateral.asset)); // 18 decimals
        int256 collateralUSDValue = weightedWadCollateralBalance.wadMul(pricePerUnit);

        return collateralUSDValue;
    }
}

contracts/test/TestPerpetual.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity 0.8.4;

// contracts
import {Perpetual} from "../Perpetual.sol";

// interfaces
import {ICryptoSwap} from "../interfaces/ICryptoSwap.sol";
import {IVBase} from "../interfaces/IVBase.sol";
import {IVQuote} from "../interfaces/IVQuote.sol";
import {IClearingHouse} from "../interfaces/IClearingHouse.sol";

// libraries
import {LibPerpetual} from "../lib/LibPerpetual.sol";

/// @notice Emitted when the given token index is gt 1
error TestPerpetual_InvalidTokenIndex(uint256 idx);

/// @notice Emitted when the initial buy amount is less than the target position size
error TestPerpetual_BuyAmountTooSmall();

/*
 * TestPerpetual includes some setter functions to edit part of
 * the internal state of Perpetual which aren't exposed otherwise.
 */
contract TestPerpetual is Perpetual {
    // emit event which can be cached in tests
    event SwapForExact(uint256 boughtVBaseTokens, uint256 additionalTokens, uint256 additionalProceeds);

    constructor(
        IVBase _vBase,
        IVQuote _vQuote,
        ICryptoSwap _curvePool,
        IClearingHouse _clearingHouse,
        int256 _insuranceFee,
        int256 _tradingFee
    ) Perpetual(_vBase, _vQuote, _curvePool, _clearingHouse, _insuranceFee, _tradingFee) {}

    // simplified setter
    function __TestPerpetual_setGlobalPosition(uint128 timeOfLastTrade, int256 cumFundingRate) external {
        globalPosition.timeOfLastTrade = timeOfLastTrade;
        globalPosition.cumFundingRate = cumFundingRate;
    }

    function __TestPerpetual_setTraderPosition(
        address trader,
        int256 openNotional,
        int256 positionSize,
        int256 cumFundingRate
    ) external {
        traderPosition[trader] = LibPerpetual.TraderPosition({
            openNotional: openNotional,
            positionSize: positionSize,
            cumFundingRate: cumFundingRate
        });
    }

    function __TestPerpetual_manipulate_market(
        uint256 tokenToSell,
        uint256 tokenToBuy,
        uint256 amountToSell
    ) external returns (uint256) {
        if (tokenToSell >= 2) revert TestPerpetual_InvalidTokenIndex(tokenToSell);
        if (tokenToBuy >= 2) revert TestPerpetual_InvalidTokenIndex(tokenToBuy);

        if (tokenToSell == VQUOTE_INDEX) {
            vQuote.mint(amountToSell);
        } else {
            vBase.mint(amountToSell);
        }

        return market.exchange(tokenToSell, tokenToBuy, amountToSell, 0);
    }

    function __TestPerpetual_swap_for_exact(uint256 proposedAmount, uint256 targetPositionSize) external {
        // mint tokens

        // console.log("__TestPerpetual_swap_for_exact");
        // console.log("block.number", block.number);
        // console.log("block.timestamp", block.timestamp);

        vQuote.mint(proposedAmount);

        uint256 boughtVBaseTokens = _quoteForBase(proposedAmount, 0);

        if (boughtVBaseTokens < targetPositionSize) revert TestPerpetual_BuyAmountTooSmall();

        uint256 baseRemaining = boughtVBaseTokens - targetPositionSize;

        // console.log("hardhat: __TestPerpetual_swap_for_exact: baseRemaining");
        // console.log(baseRemaining);

        uint256 additionalProceeds;
        if (_canSellBase(baseRemaining)) {
            // sell vBase tokens bought in excess
            additionalProceeds = _baseForQuote(baseRemaining, 0);

            // console.log("hardhat: __TestPerpetual_swap_for_exact: additionalProceeds");
            // console.log(additionalProceeds);
        }

        emit SwapForExact(boughtVBaseTokens, baseRemaining, additionalProceeds);
    }

    function __TestPerpetual_updateTwapAndFunding() external {
        _updateTwapAndFundingRate();
    }

    function __TestPerpetual_updateFunding() external {
        _updateFundingRate();
    }

    function __TestPerpetual_updateTwap() external {
        _updateTwap();
    }

    function __TestPerpetual_setTWAP(int256 _marketTwap, int256 _oracleTwap) external {
        marketTwap = _marketTwap;
        oracleTwap = _oracleTwap;
    }

    function __TestPerpetual_setBlockStartPrice(uint256 blockStartPrice) external {
        globalPosition.blockStartPrice = int256(blockStartPrice);
    }

    function __TEST__checkPriceDeviation(int256 _marketPrice, int256 _oraclePrice) external pure returns (bool) {
        return _checkPriceDeviation(_marketPrice, _oraclePrice);
    }

    function getOracleCumulativeAmount() external view returns (int256) {
        return oracleCumulativeAmount;
    }

    function getOracleCumulativeAmountAtBeginningOfPeriod() external view returns (int256) {
        return oracleCumulativeAmountAtBeginningOfPeriod;
    }

    function getMarketCumulativeAmount() external view returns (int256) {
        return marketCumulativeAmount;
    }

    function getMarketCumulativeAmountAtBeginningOfPeriod() external view returns (int256) {
        return marketCumulativeAmountAtBeginningOfPeriod;
    }
}

contracts/Perpetual.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity 0.8.4;

// contracts
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {Pausable} from "@openzeppelin/contracts/security/Pausable.sol";

// interfaces
import {IPerpetual} from "./interfaces/IPerpetual.sol";
import {IVBase} from "./interfaces/IVBase.sol";
import {IVQuote} from "./interfaces/IVQuote.sol";
import {ICryptoSwap} from "./interfaces/ICryptoSwap.sol";
import {IClearingHouse} from "./interfaces/IClearingHouse.sol";

// libraries
import {LibMath} from "./lib/LibMath.sol";
import {LibPerpetual} from "./lib/LibPerpetual.sol";

import "hardhat/console.sol";

/// @notice Handles all the trading logic. Interact with the CryptoSwap pool
contract Perpetual is IPerpetual, Pausable {
    using LibMath for int256;
    using LibMath for uint256;

    // parameterization
    uint256 internal constant VQUOTE_INDEX = 0; // index of quote asset in curve pool
    uint256 internal constant VBASE_INDEX = 1; // index of base asset in curve pool
    uint256 internal constant TWAP_FREQUENCY = 15 minutes; // period over which twap is calculated
    int256 internal constant SENSITIVITY = 1e18; // funding rate sensitivity to price deviations
    int256 internal constant MAX_PRICE_DEVIATION = 5e16; // max price change per block
    uint256 internal constant CURVE_TRADING_FEE_PRECISION = 1e10; // curve trading fee precision

    int256 internal tradingFee; // fee charged for each trade on the notional amount
    int256 internal insuranceFee; // paid on dollar value of an opened position (used in ClearingHouse)

    // dependencies

    /// @notice vBase token (traded on CryptoSwap pool)
    IVBase public override vBase;

    /// @notice vQuote token (traded on CryptoSwap pool)
    IVQuote public override vQuote;

    /// @notice Clearing House contract
    IClearingHouse public override clearingHouse;

    /// @notice Curve CryptoSwap pool
    ICryptoSwap public override market;

    // global state
    LibPerpetual.GlobalPosition internal globalPosition;

    int256 internal oracleCumulativeAmount;
    int256 internal oracleCumulativeAmountAtBeginningOfPeriod;
    int256 internal oracleTwap;

    int256 internal marketCumulativeAmount;
    // slither-disable-next-line similar-names
    int256 internal marketCumulativeAmountAtBeginningOfPeriod;
    int256 internal marketTwap;

    // user state
    mapping(address => LibPerpetual.TraderPosition) internal traderPosition;
    mapping(address => LibPerpetual.LiquidityProviderPosition) internal lpPosition;

    /* ****************** */
    /*     Events         */
    /* ****************** */

    /// @notice Emitted when twap is updated
    /// @param newOracleTwap Latest oracle Time-weighted-average-price
    /// @param newMarketTwap Latest market Time-weighted-average-price
    event TwapUpdated(int256 newOracleTwap, int256 newMarketTwap);

    /// @notice Emitted when funding rate is updated
    /// @param cumulativeFundingRate Cumulative sum of all funding rate updates
    /// @param fundingRate Latest fundingRate update
    event FundingRateUpdated(int256 cumulativeFundingRate, int256 fundingRate);

    /// @notice Emitted when swap with cryptoswap pool fails
    /// @param errorMessage Return error message
    event Log(string errorMessage);

    /// @notice Emitted when (base) dust is generated
    /// @param vBaseAmount Amount of dust
    event DustGenerated(uint256 vBaseAmount);

    /// @notice Emitted when insuranceFee is changed
    /// @param newInsuranceFee New value of insurance fee
    event InsuranceFeeChanged(int256 newInsuranceFee);

    /// @notice Emitted when tradingFee is changed
    /// @param newTradingFee New value of trading fee
    event TradingFeeChanged(int256 newTradingFee);

    constructor(
        IVBase _vBase,
        IVQuote _vQuote,
        ICryptoSwap _market,
        IClearingHouse _clearingHouse,
        int256 _insuranceFee,
        int256 _perpTradingFee
    ) {
        if (address(_vBase) == address(0)) revert Perpetual_ZeroAddressConstructor(0);
        if (address(_vQuote) == address(0)) revert Perpetual_ZeroAddressConstructor(1);
        if (address(_market) == address(0)) revert Perpetual_ZeroAddressConstructor(2);
        if (address(_clearingHouse) == address(0)) revert Perpetual_ZeroAddressConstructor(3);

        vBase = _vBase;
        vQuote = _vQuote;
        market = _market;
        clearingHouse = _clearingHouse;

        // approve all future transfers between Perpetual and market (curve pool)
        if (!vBase.approve(address(_market), type(uint256).max))
            revert Perpetual_VirtualTokenApprovalConstructor(VBASE_INDEX);
        if (!vQuote.approve(address(_market), type(uint256).max))
            revert Perpetual_VirtualTokenApprovalConstructor(VQUOTE_INDEX);

        // initialize global state
        _initGlobalState(_vBase.getIndexPrice(), _market.last_prices().toInt256());

        // set initial parameter value for the market
        setInsuranceFee(_insuranceFee);
        setTradingFee(_perpTradingFee);

        if (market.mid_fee() != market.out_fee()) revert Perpetual_MarketEqualFees();
    }

    modifier onlyClearingHouse() {
        if (msg.sender != address(clearingHouse)) revert Perpetual_SenderNotClearingHouse();
        _;
    }

    // the owner of the market is the same owner as ClearingHouse
    modifier onlyClearingHouseOwner() {
        if (msg.sender != clearingHouse.getOwner()) revert Perpetual_SenderNotClearingHouseOwner();
        _;
    }

    /* ****************** */
    /*   Trader flow      */
    /* ****************** */

    /// @notice Open or increase a position, either long or short
    /// @param account Address of the trader
    /// @param amount to be sold, in vQuote (if long) or vBase (if short)
    /// @param direction Long or Short
    /// @param minAmount Minimum amount received back, in vBase (if long) or vQuote (if short)
    /// @return openNotional Additional quote asset / liabilities accrued
    /// @return positionSize Additional base asset / liabilities accrued
    /// @return tradingFees Settled funding payments & Trading
    function extendPosition(
        address account,
        uint256 amount,
        LibPerpetual.Side direction,
        uint256 minAmount
    )
        external
        override
        whenNotPaused
        onlyClearingHouse
        returns (
            int256 openNotional,
            int256 positionSize,
            int256 tradingFees
        )
    {
        /*
            if direction = LONG

                trader goes long EUR
                trader accrues openNotional debt
                trader receives positionSize assets

                openNotional = vQuote traded to market    ( < 0)
                positionSize = vBase received from market ( > 0)

            else direction = SHORT

                trader goes short EUR
                trader receives openNotional assets
                trader accrues positionSize debt

                openNotional = vQuote received from market ( > 0)
                positionSize = vBase traded to market      ( < 0)

        */
        LibPerpetual.TraderPosition storage trader = traderPosition[account];
        bool isLong = direction == LibPerpetual.Side.Long;

        // make sure trader doesn't try to use `extendPosition` to reduce a position
        // to skip the settlement of due funding payments
        if (
            (isLong && traderPosition[account].positionSize < 0) ||
            (!isLong && traderPosition[account].positionSize > 0)
        ) revert Perpetual_AttemptReducePosition();

        // open position
        (openNotional, positionSize) = _extendPosition(amount, isLong, minAmount);

        // check max deviation
        if (!_checkPriceDeviation(marketPrice().toInt256(), globalPosition.blockStartPrice))
            revert Perpetual_ExcessivePriceImpact();

        // update position
        tradingFees = -_chargeQuoteFees(openNotional);
        trader.openNotional += openNotional;
        trader.positionSize += positionSize;

        return (openNotional, positionSize, tradingFees);
    }

    /// @notice Closes position from account holder
    /// @param account Trader account to close position for.
    /// @param proposedAmount Amount of tokens to be sold, in vBase if LONG, in vQuote if SHORT. 18 decimals
    /// @param minAmount Minimum amount that the user is willing to accept, in vQuote if LONG, in vBase if SHORT. 18 decimals
    /// @return vQuoteProceeds Realized quote proceeds from closing the position
    /// @return vBaseAmount Position size reduction
    /// @return profit Profit realized
    function reducePosition(
        address account,
        uint256 proposedAmount,
        uint256 minAmount
    )
        external
        override
        whenNotPaused
        onlyClearingHouse
        returns (
            int256 vQuoteProceeds,
            int256 vBaseAmount,
            int256 profit
        )
    {
        /*
        after opening the position:

            trader has long position:
                openNotional = vQuote traded   to market   ( < 0)
                positionSize = vBase  received from market ( > 0)
            trader has short position
                openNotional = vQuote received from market ( > 0)
                positionSize = vBase  traded   to market   ( < 0)

        to close the position:

            trader has long position:
                @proposedAmount := amount of vBase used to reduce the position (must be below user.positionSize)
                => User trades the vBase tokens with the curve pool for vQuote tokens

            trader has short position:
                @proposedAmount := amount of vQuote required to repay the vBase debt (must be below 1.5 x market value of user.positionSize)
                => User incurred vBase debt when opening a position and must now trade enough
                  vQuote with the curve pool to repay his vBase debt in full.
                => Remaining balances can be traded with the market for vQuote.

                @audit Note that this mechanism can be exploited by inserting a large value here, since traders
                will have to pay transaction fees anyways (on the curve pool). We set a limit of 1.5 x market value in _checkProposedAmount()
        */
        LibPerpetual.TraderPosition storage trader = traderPosition[account];
        if (trader.openNotional == 0 && trader.positionSize == 0) revert Perpetual_NoOpenPosition();

        int256 pnl;
        (vBaseAmount, vQuoteProceeds, pnl) = _reducePosition(trader, proposedAmount, minAmount);

        // check max deviation
        if (!_checkPriceDeviation(marketPrice().toInt256(), globalPosition.blockStartPrice))
            revert Perpetual_ExcessivePriceImpact();

        // adjust trader position
        int256 quoteOnlyFees = _chargeQuoteFees(vQuoteProceeds);
        profit = pnl - quoteOnlyFees; // pnl + fundingPayments - quoteOnlyFees

        trader.openNotional += vQuoteProceeds;
        trader.positionSize += vBaseAmount;

        // if position has been closed entirely, delete it from the state
        if (trader.positionSize == 0) {
            delete traderPosition[account];
        }

        return (vQuoteProceeds, vBaseAmount, profit);
    }

    function settleTrader(address account)
        external
        override
        onlyClearingHouse
        whenNotPaused
        returns (int256 fundingPayments)
    {
        LibPerpetual.TraderPosition storage trader = traderPosition[account];
        LibPerpetual.GlobalPosition storage global = globalPosition;

        // update global state
        _updateTwapAndFundingRate();

        // apply funding rate on existing positionSize
        if (trader.positionSize != 0) {
            // settle trader funding rate
            fundingPayments = _getFundingPayments(
                trader.positionSize > 0,
                trader.cumFundingRate,
                global.cumFundingRate,
                trader.positionSize.abs()
            );
        }

        trader.cumFundingRate = global.cumFundingRate;

        return fundingPayments;
    }

    /* ******************************/
    /*     Liquidity provider flow  */
    /* ******************************/

    /// @notice Provide liquidity to the pool
    /// @param account Liquidity provider
    /// @param wadAmount Amount of vQuote provided. 18 decimals
    /// @param minLpAmount Minimum amount of Lp tokens minted. 18 decimals
    /// @return tradingFees Generated profit generated from trading fees
    function provideLiquidity(
        address account,
        uint256 wadAmount,
        uint256 minLpAmount
    ) external override whenNotPaused onlyClearingHouse returns (int256 tradingFees) {
        _updateTwapAndFundingRate();

        // reflect the added liquidity on the LP position
        LibPerpetual.LiquidityProviderPosition storage lp = lpPosition[account];
        LibPerpetual.GlobalPosition storage global = globalPosition;

        // update when has provided liquidity before
        if (lp.liquidityBalance != 0) {
            tradingFees = _settleLpTradingFees(lp, global).toInt256();

            // With previous liquidity providers, we must update the `totalBaseFeesGrowth` & `totalQuoteFeesGrowth` parameter
            // However, we do not account that for `older` liquidity we must still burn a larger share of the tokens
            // SOLUTION: Burn fees earned so far.
            (int256 baseFeesEarned, int256 quoteFeesEarned) = _getVirtualTokensEarnedAsCurveTradingFees(lp, global);
            lp.openNotional -= quoteFeesEarned;
            lp.positionSize -= baseFeesEarned;
        }

        uint256 basePrice;
        if (getTotalLiquidityProvided() == 0) {
            basePrice = marketPrice();
        } else {
            basePrice = market.balances(0).wadDiv(market.balances(1));
        }
        uint256 baseAmount = wadAmount.wadDiv(basePrice); // vQuote / vBase/vQuote  <=> 1 / 1.2 = 0.8

        // supply liquidity to curve pool
        vQuote.mint(wadAmount);
        vBase.mint(baseAmount);

        // Question: Should we compensate liquidity providers for the fee proportion in the pool (let them provider leveraged liquidity)
        // Answer: No, the relative difference of earlier and later liquidity providers do not matter. We only care for the pnL of them.
        uint256 liquidity = market.add_liquidity([wadAmount, baseAmount], minLpAmount); //  first token in curve pool is vQuote & second token is vBase

        // update state
        lp.openNotional -= wadAmount.toInt256();
        lp.positionSize -= baseAmount.toInt256();
        lp.cumFundingRate = globalPosition.cumFundingRate;

        lp.totalTradingFeesGrowth = globalPosition.totalTradingFeesGrowth;
        lp.totalBaseFeesGrowth = globalPosition.totalBaseFeesGrowth;
        lp.totalQuoteFeesGrowth = globalPosition.totalQuoteFeesGrowth;

        lp.cumFundingRate = globalPosition.cumFundingRate;

        lp.liquidityBalance += liquidity;

        global.totalLiquidityProvided += wadAmount;
    }

    /// @notice Remove liquidity from the pool
    /// @param account Account of the LP to remove liquidity from
    /// @param liquidityAmountToRemove Amount of liquidity to be removed from the pool. 18 decimals
    /// @param minVTokenAmounts Minimum amount of virtual tokens [vQuote, vBase] withdrawn from the curve pool. 18 decimals
    /// @param proposedAmount Amount of tokens to be sold, in vBase if LONG, in vQuote if SHORT. 18 decimals
    /// @param minAmount Minimum amount that the user is willing to accept, in vQuote if LONG, in vBase if SHORT. 18 decimals
    /// @return profit Profit realized
    function removeLiquidity(
        address account,
        uint256 liquidityAmountToRemove,
        uint256[2] calldata minVTokenAmounts,
        uint256 proposedAmount,
        uint256 minAmount
    ) external override whenNotPaused onlyClearingHouse returns (int256 profit, uint256 reductionRatio) {
        LibPerpetual.LiquidityProviderPosition storage lp = lpPosition[account];
        LibPerpetual.GlobalPosition storage global = globalPosition;

        _updateTwapAndFundingRate();

        // slither-disable-next-line incorrect-equality
        if (liquidityAmountToRemove > lp.liquidityBalance) revert Perpetual_LPWithdrawExceedsBalance();

        profit += _settleLpTradingFees(lp, global).toInt256();
        // remove liquidity from curve pool
        (uint256 quoteAmount, uint256 baseAmount) = _removeLiquidity(
            lp,
            global,
            liquidityAmountToRemove,
            minVTokenAmounts
        );

        // settle previous active position
        reductionRatio = liquidityAmountToRemove.wadDiv(lp.liquidityBalance);

        (int256 pnl, int256 quoteProceeds) = _settleLpPosition(
            LibPerpetual.TraderPosition({
                openNotional: quoteAmount.toInt256() + lp.openNotional.wadMul(reductionRatio.toInt256()),
                positionSize: baseAmount.toInt256() + lp.positionSize.wadMul(reductionRatio.toInt256()),
                cumFundingRate: 0
            }),
            proposedAmount,
            minAmount
        );
        int256 quoteOnlyFees = _chargeQuoteFees(quoteProceeds);
        profit += pnl - quoteOnlyFees;

        // adjust balances to new position
        lp.openNotional += quoteAmount.toInt256();
        lp.positionSize += baseAmount.toInt256();
        lp.liquidityBalance -= liquidityAmountToRemove;

        lp.cumFundingRate = global.cumFundingRate;

        // if position has been closed entirely, delete it from the state
        // slither-disable-next-line incorrect-equality
        if (lp.liquidityBalance == 0) {
            delete lpPosition[account];
        }

        // Question: How should we think about this parameter?
        global.totalLiquidityProvided -= quoteAmount;
    }

    function settleLp(address account)
        external
        override
        whenNotPaused
        onlyClearingHouse
        returns (int256 fundingPayments)
    {
        LibPerpetual.LiquidityProviderPosition storage lp = lpPosition[account];
        LibPerpetual.GlobalPosition storage global = globalPosition;

        // update global state
        _updateTwapAndFundingRate();

        if (lp.liquidityBalance == 0) {
            return 0;
        }

        // settle lp funding rate
        int256 virtualPositionSize = _getVBasePositionAfterVirtualWithdrawal(lp, global);

        fundingPayments = _getFundingPayments(
            virtualPositionSize > 0,
            lp.cumFundingRate,
            global.cumFundingRate,
            virtualPositionSize.abs()
        );

        lp.cumFundingRate = global.cumFundingRate;

        return fundingPayments;
    }

    ///// COMMON OPERATIONS \\\\\

    /// @notice Update Twap, Funding Rate and last traded price
    // TODO: combine functions
    function _updateTwapAndFundingRate() internal whenNotPaused {
        LibPerpetual.GlobalPosition storage global = globalPosition;
        uint256 currentTime = block.timestamp;
        uint256 timeOfLastTrade = uint256(global.timeOfLastTrade);

        // Don't update the state more than once per block
        // slither-disable-next-line timestamp
        if (currentTime > timeOfLastTrade) {
            _recordMarketPrice();
            _updateTwap();
            _updateFundingRate();
        }
    }

