// SPDX-License-Identifier: GPL-3.0

pragma solidity >=0.7.0 <0.9.0;

import "hardhat/console.sol";

contract Increment {
    constructor() {}

    function loopSafe(uint256 len) public view returns (uint256) {
        uint256 iter = 0;
        for (uint256 i = len; i > 0; i--) {
            console.log("i-1 is", i - 1);

            iter += 1;
        }
        return iter;
    }

    function loopUnsafe(uint256 len) public view returns (uint256) {
        uint256 iter = 0;
        for (uint256 i = len - 1; i >= 0; i--) {
            console.log("i is", i);

            iter += 1;
        }
        return iter;
    }
}

