// SPDX-License-Identifier: AGPL-3.0
pragma solidity 0.8.4;

// contracts
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Vault} from "../Vault.sol";

// interfaces
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

/*
 * TestVault includes some setter functions to edit part of
 * the internal state of Vault which aren't exposed otherwise.
 */
contract TestVault is Vault {
    using SafeERC20 for IERC20Metadata;

    constructor(IERC20Metadata _ua) Vault(_ua) {}

    /// @notice Set trader balance without any actual token transfer
    function __TestVault_set_trader_balance(
        address user,
        uint256 marketIdx,
        uint256 tokenIdx,
        int256 amount
    ) external {
        return _changeBalance(user, marketIdx, tokenIdx, amount, true);
    }

    /// @notice Empty out the vault without adjusting the internal accounting
    function __TestVault_transfer_out(
        address user,
        IERC20Metadata withdrawToken,
        uint256 amount
    ) external {
        IERC20Metadata(withdrawToken).safeTransfer(user, amount);
    }

    /// @notice Liquidate collateral without having to close a position first
    function __TestVaultSettleLiquidationOnCollaterals(
        address liquidator,
        address liquidatee,
        uint256 marketIdx,
        bool isTrader
    ) external {
        _settleLiquidationOnCollaterals(liquidator, liquidatee, marketIdx, isTrader);
    }
}

contracts/ClearingHouseViewer.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity 0.8.4;

// contracts
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// interfaces
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {IClearingHouse} from "./interfaces/IClearingHouse.sol";
import {IClearingHouseViewer} from "./interfaces/IClearingHouseViewer.sol";
import {ICryptoSwap} from "./interfaces/ICryptoSwap.sol";

// libraries
import {LibMath} from "./lib/LibMath.sol";
import {LibPerpetual} from "./lib/LibPerpetual.sol";

/// @title Clearing House Helper Contract
/// @notice Utility functions to easily extract data from Perpetual Contracts
contract ClearingHouseViewer is IClearingHouseViewer {
    using LibMath for uint256;
    using LibMath for int256;
    using SafeERC20 for IERC20Metadata;

    // dependencies
    IClearingHouse public clearingHouse;

    uint256 private constant VQUOTE_INDEX = 0;
    uint256 private constant VBASE_INDEX = 1;

    constructor(IClearingHouse _clearingHouse) {
        if (address(_clearingHouse) == address(0)) revert ClearingHouseViewer_ZeroAddressConstructor(0);
        clearingHouse = _clearingHouse;
    }

    /// @notice Return amount for vBase one would receive for exchanging `vQuoteAmountToSpend` in a select market (excluding slippage)
    /// @dev It's up to the client to apply a reduction of this amount (e.g. -1%) to then use it as `minAmount` in `extendPosition`
    /// @dev TODO: store market address to avoid second call
    /// @param idx Index of the perpetual market
    /// @param vQuoteAmountToSpend Amount of vQuote to be exchanged against some vBase. 18 decimals
    function getExpectedVBaseAmount(uint256 idx, uint256 vQuoteAmountToSpend) public view override returns (uint256) {
        // TODO: add quote only fees
        // slither-disable-next-line calls-loop
        return clearingHouse.perpetuals(idx).market().get_dy(VQUOTE_INDEX, VBASE_INDEX, vQuoteAmountToSpend);
    }

    /// @notice Return amount for vQuote one would receive for exchanging `vBaseAmountToSpend` in a select market (excluding slippage)
    /// @dev It's up to the client to apply a reduction of this amount (e.g. -1%) to then use it as `minAmount` in `extendPosition`
    /// @dev TODO: store market address to avoid second call
    /// @param idx Index of the perpetual market
    /// @param vBaseAmountToSpend Amount of vBase to be exchanged against some vQuote. 18 decimals
    function getExpectedVQuoteAmount(uint256 idx, uint256 vBaseAmountToSpend) public view override returns (uint256) {
        return clearingHouse.perpetuals(idx).market().get_dy(VBASE_INDEX, VQUOTE_INDEX, vBaseAmountToSpend);
    }

    /// @notice Return the last traded price (used for TWAP)
    /// @param idx Index of the perpetual market
    function marketPrice(uint256 idx) public view override returns (uint256) {
        return clearingHouse.perpetuals(idx).marketPrice();
    }

    /// @notice Return the current off-chain exchange rate for vBase/vQuote
    /// @param idx Index of the perpetual market
    function indexPrice(uint256 idx) external view override returns (int256) {
        return clearingHouse.perpetuals(idx).indexPrice();
    }

    /// @param idx Index of the perpetual market
    function getGlobalPosition(uint256 idx) external view override returns (LibPerpetual.GlobalPosition memory) {
        return clearingHouse.perpetuals(idx).getGlobalPosition();
    }

    /// @param idx Index of the perpetual market
    function getMarket(uint256 idx) external view override returns (ICryptoSwap) {
        return clearingHouse.perpetuals(idx).market();
    }

    function getInsuranceFee(uint256 idx) external view override returns (int256) {
        return clearingHouse.perpetuals(idx).getInsuranceFee();
    }

    /* ****************** */
    /*   User viewer      */
    /* ****************** */

    /// @notice Approximately determine whether or not a position is valid for a given margin ratio (incl. an estimate of pending funding payments)
    /// @param idx Index of the perpetual market
    /// @param account Account of the position to get the margin ratio from
    /// @param ratio Proposed ratio to compare the position against
    /// @param isTrader True if the user is a trader, False if the user is a liquidity provider
    /// @return True if the position exceeds margin ratio, false otherwise
    function marginIsValid(
        uint256 idx,
        address account,
        int256 ratio,
        bool isTrader
    ) external view override returns (bool) {
        return marginRatio(idx, account, isTrader) >= ratio;
    }

    /// @notice Get the (approximate) margin ratio of a trading position (given that, for now, 1 trading position = 1 address) incl. an estimate of pending funding payments
    /// @param idx Index of the perpetual market
    /// @param account Account of the position to get the margin ratio from
    /// @param isTrader True if the user is a trader, False if the user is a liquidity provider
    /// @return Margin ratio of the position (in 1e18)
    function marginRatio(
        uint256 idx,
        address account,
        bool isTrader
    ) public view override returns (int256) {
        // margin ratio = (collateral + unrealizedPositionPnl) / trader.openNotional
        // all amounts must be expressed in vQuote (e.g. USD), otherwise the end result doesn't make sense
        int256 openNotional = isTrader
            ? getTraderPosition(idx, account).openNotional
            : getLpPositionAfterWithdrawal(idx, account).openNotional;

        // when no position open, margin ratio is 100%
        if (openNotional == 0) {
            return 1e18;
        }

        int256 collateral = isTrader ? getTraderReserveValue(idx, account) : getLpReserveValue(idx, account);
        int256 unrealizedPositionPnl = isTrader
            ? getTraderUnrealizedPnL(idx, account)
            : getLpUnrealizedPnL(idx, account);
        int256 fundingPayments = isTrader ? getTraderFundingPayments(idx, account) : getLpFundingPayments(idx, account);

        return _computeMarginRatio(collateral, unrealizedPositionPnl, fundingPayments, openNotional);
    }

    /// @notice Calculate missed funding payments
    /// @param idx Index of the perpetual market
    /// @param account Trader to get the funding payments
    function getTraderFundingPayments(uint256 idx, address account) public view override returns (int256) {
        return clearingHouse.perpetuals(idx).getTraderFundingPayments(account);
    }

    /// @param idx Index of the perpetual market
    /// @param account Trader to get the unrealized PnL from
    function getTraderUnrealizedPnL(uint256 idx, address account) public view override returns (int256) {
        return clearingHouse.perpetuals(idx).getTraderUnrealizedPnL(account);
    }

    /// @notice Get the portfolio value of a trader
    /// @param idx Index of the perpetual market
    /// @param account Address to get the portfolio value from
    function getTraderReserveValue(uint256 idx, address account) public view override returns (int256) {
        return clearingHouse.vault().getTraderReserveValue(idx, account);
    }

    /// @notice Get trader position
    /// @param idx Index of the perpetual market
    /// @param account Address to get the trading position from
    function getTraderPosition(uint256 idx, address account)
        public
        view
        override
        returns (LibPerpetual.TraderPosition memory)
    {
        return clearingHouse.perpetuals(idx).getTraderPosition(account);
    }

    /// @notice Get trader balance
    /// @param user Trader to get the balance of
    /// @param marketIdx Index of the perpetual market
    /// @param tokenIdx Token to get the balance of
    function getTraderBalance(
        address user,
        uint256 marketIdx,
        uint256 tokenIdx
    ) external view override returns (int256) {
        return clearingHouse.vault().getTraderBalance(user, marketIdx, tokenIdx);
    }

    /// @notice Get LP balance
    /// @param user LP to get the balance of
    /// @param marketIdx Index of the perpetual market
    /// @param tokenIdx Token to get the balance of
    function getLpBalance(
        address user,
        uint256 marketIdx,
        uint256 tokenIdx
    ) external view override returns (int256) {
        return clearingHouse.vault().getLpBalance(user, marketIdx, tokenIdx);
    }

    /// @notice Calculate missed funding payments
    /// @param idx Index of the perpetual market
    /// @param account Lp to get the funding payments
    function getLpFundingPayments(uint256 idx, address account) public view override returns (int256) {
        return clearingHouse.perpetuals(idx).getLpFundingPayments(account);
    }

    /// @param idx Index of the perpetual market
    /// @param account Lp to get the unrealized PnL from
    function getLpUnrealizedPnL(uint256 idx, address account) public view override returns (int256) {
        return clearingHouse.perpetuals(idx).getLpUnrealizedPnL(account);
    }

    /// @notice Get the portfolio value of an Lp
    /// @param idx Index of the perpetual market
    /// @param account Address to get the portfolio value from
    function getLpReserveValue(uint256 idx, address account) public view override returns (int256) {
        return clearingHouse.vault().getLpReserveValue(idx, account);
    }

    /// @notice Get the (active) position of a liquidity provider after withdrawing liquidity
    /// @param account Liquidity Provider
    /// @return (Active) Liquidity Provider position
    function getLpPositionAfterWithdrawal(uint256 idx, address account)
        public
        view
        override
        returns (LibPerpetual.TraderPosition memory)
    {
        return clearingHouse.perpetuals(idx).getLpPositionAfterWithdrawal(account);
    }

    /// @notice Get Lp position
    /// @param idx Index of the perpetual market
    /// @param account Address to get the LP position from
    function getLpPosition(uint256 idx, address account)
        external
        view
        override
        returns (LibPerpetual.LiquidityProviderPosition memory)
    {
        return clearingHouse.perpetuals(idx).getLpPosition(account);
    }

    /// @notice Get the current (base) dust balance
    /// @return Base balance of Governance. 18 decimals
    function getBaseDust(uint256 idx) external view override returns (uint256) {
        return getTraderPosition(idx, address(clearingHouse)).positionSize.toUint256();
    }

    /// @notice Get the proposed amount needed to close a position
    /// @dev Solidity implementation to minimize the node calls once has to make when finding proposed amount
    /// @dev Should not be called from another contract
    /// @param idx Index of the perpetual market
    /// @param trader Account
    /// @param iter Maximum iterations
    /// @return amountIn Amount of tokens to swap. 18 decimals
    /// @return amountOut Amount of tokens to receive from the swap. 18 decimals
    // TODO: use reductionRatio parameters
    // TODO: adjust for curve fees reimbursed
    function getProposedAmount(
        uint256 idx,
        address trader,
        uint256 iter
    ) external view override returns (uint256 amountIn, uint256 amountOut) {
        int256 positionSize = getTraderPosition(idx, trader).positionSize;
        if (positionSize > 0) {
            amountIn = positionSize.toUint256();
            amountOut = getExpectedVQuoteAmount(idx, amountIn);
        } else {
            uint256 position = (-positionSize).toUint256();
            amountIn = position.wadMul(marketPrice(idx));
            // binary search in [marketPrice * 0.7, marketPrice * 1.3]
            uint256 maxVal = (amountIn * 13) / 10;
            uint256 minVal = (amountIn * 7) / 10;

            for (uint256 i = 0; i < iter; i++) {
                amountIn = (minVal + maxVal) / 2;
                // slither-disable-next-line calls-loop
                amountOut = getExpectedVBaseAmount(idx, amountIn);

                if (amountOut == position) {
                    break;
                } else if (amountOut < position) {
                    minVal = amountIn;
                } else {
                    maxVal = amountIn;
                }
            }

            // take maxVal to make sure we are above the target
            if (amountOut < position) {
                amountIn = maxVal;
                amountOut = getExpectedVBaseAmount(idx, amountIn);
            }
        }
        return (amountIn, amountOut);
    }

    /* ****************** */
    /*   Internal viewer  */
    /* ****************** */

    function _computeMarginRatio(
        int256 collateral,
        int256 unrealizedPositionPnl,
        int256 fundingPayments,
        int256 openNotional
    ) internal pure returns (int256) {
        return (collateral + unrealizedPositionPnl + fundingPayments).wadDiv(openNotional.abs());
    }
}

