import {IVault} from "./IVault.sol";
import {ICryptoSwap} from "./ICryptoSwap.sol";
import {IInsurance} from "./IInsurance.sol";

// libraries
import {LibPerpetual} from "../lib/LibPerpetual.sol";

interface IClearingHouseViewer {
    /* ****************** */
    /*    Errors          */
    /* ****************** */

    /// @notice Emitted when the zero address is provided as a parameter in the constructor
    error ClearingHouseViewer_ZeroAddressConstructor(uint8 paramIndex);

    /* ****************** */
    /*    Global Getters  */
    /* ****************** */

    function getExpectedVBaseAmount(uint256 idx, uint256 vQuoteAmountToSpend) external view returns (uint256);

    function getExpectedVQuoteAmount(uint256 idx, uint256 vBaseAmountToSpend) external view returns (uint256);

    function marketPrice(uint256 idx) external view returns (uint256);

    function indexPrice(uint256 idx) external view returns (int256);

    function getGlobalPosition(uint256 idx) external view returns (LibPerpetual.GlobalPosition memory);

    function getBaseDust(uint256 idx) external view returns (uint256);

    function getMarket(uint256 idx) external view returns (ICryptoSwap);

    function insuranceFee(uint256 idx) external view returns (int256);

    function getBaseBalance(uint256 idx) external view returns (uint256);

    function getQuoteBalance(uint256 idx) external view returns (uint256);

    function getTotalLiquidityProvided(uint256 idx) external view returns (uint256);

    /* ****************** */
    /*    User Getters    */
    /* ****************** */

    function isMarginValid(address account, int256 ratio) external view returns (bool);

    function marginRatio(address account) external view returns (int256);

    function getFundingPaymentsAcrossMarkets(address account) external view returns (int256 fundingPayments);

    function getReserveValue(address account, bool isDiscounted) external view returns (int256);

    function getBalance(address user, uint256 tokenIdx) external view returns (int256);

    function getTraderFundingPayments(uint256 idx, address account) external view returns (int256);

    function getTraderUnrealizedPnL(uint256 idx, address account) external view returns (int256);

    function getTraderPosition(uint256 idx, address account) external view returns (LibPerpetual.TraderPosition memory);

    function getLpFundingPayments(uint256 idx, address account) external view returns (int256);

    function getLpEstimatedPnl(uint256 idx, address account) external view returns (int256);

    function getLpTradingFees(uint256 idx, address account) external view returns (uint256);

    function getLpUnrealizedPnL(uint256 idx, address account) external view returns (int256);

    function isTraderPositionOpen(uint256 idx, address account) external view returns (bool);

    function isLpPositionOpen(uint256 idx, address account) external view returns (bool);

    function getLpPositionAfterWithdrawal(uint256 idx, address account)
        external
        view
        returns (LibPerpetual.TraderPosition memory);

    function getLpPosition(uint256 idx, address account)
        external
        view
        returns (LibPerpetual.LiquidityProviderPosition memory);

    function getProposedAmount(
        uint256 idx,
        address user,
        bool isTrader,
        uint256 reductionRatio,
        uint256 iter
    ) external view returns (uint256 amountIn, uint256 amountOut);

    /* ******************** */
    /*    Static Helpers    */
    /* ******************** */

    function removeLiquiditySwap(
        uint256 idx,
        address user,
        uint256 liquidityAmountToRemove,
        uint256[2] calldata minVTokenAmounts,
        uint256 proposedAmount
    ) external returns (uint256 baseProceeds);
}

contracts/CurveCryptoViews.sol.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity 0.8.15;

// interfaces
import {ICryptoSwap} from "./interfaces/ICryptoSwap.sol";
import {IMath} from "./interfaces/IMath.sol";
import {ICurveCryptoViews} from "./interfaces/ICurveCryptoViews.sol";

// libraries
import {LibMath} from "./lib/LibMath.sol";

contract CurveCryptoViews is ICurveCryptoViews {
    using LibMath for uint256;

    // constants
    uint256 private constant N_COINS = 2;
    uint256 private constant PRECISION = 10**18; //* The precision to convert to
    uint256[N_COINS] private PRECISIONS = [1, 1]; // Constants of non-value type not yet implemented.

    /// @notice Curve Math Contract
    IMath public override math;

    constructor(address _math) {
        math = IMath(_math);
    }

    /// @notice Get the amount of coin j one would receive for swapping dx of coin i (excl. fees)
    /// @param cryptoSwap Curve Cryptoswap contract
    /// @param i Index of the coin to sell
    /// @param j Index of the coin to buy
    /// @param dx Amount to sell
    /// @return Amount of tokens to received + Any trading fees payed (in j)
    /// @dev solidity implementation of the get_dy excluding the last last line where fees are deducted
    /// https://github.com/curvefi/curve-crypto-contract/blob/d7d04cd9ae038970e40be850df99de8c1ff7241b/contracts/tricrypto/CurveCryptoViews3.vy#L40-L78
    function get_dy_ex_fees(
        ICryptoSwap cryptoSwap,
        uint256 i,
        uint256 j,
        uint256 dx
    ) public view override returns (uint256) {
        require(i != j && i < N_COINS, "coin index out of range");
        require(dx > 0, "do not exchange 0 coins");

        uint256 price_scale = cryptoSwap.price_scale();
        uint256[2] memory xp = [cryptoSwap.balances(0), cryptoSwap.balances(1)];

        uint256 A = cryptoSwap.A();
        uint256 gamma = cryptoSwap.gamma();
        uint256 D = cryptoSwap.D();

        if (cryptoSwap.future_A_gamma_time() > 0) {
            D = math.newton_D(A, gamma, [xp[0] * PRECISIONS[0], (xp[1] * PRECISIONS[1] * price_scale) / PRECISION]);
        }

        xp[i] += dx;
        xp = [xp[0] * PRECISIONS[0], (xp[1] * price_scale) / PRECISION];

        uint256 y = math.newton_y(A, gamma, xp, D, j);
        uint256 dy = xp[j] - y - 1;
        xp[j] = y;
        if (j > 0) {
            dy = (dy * PRECISION) / price_scale;
        } else {
            dy /= PRECISIONS[0];
        }

        return dy;
    }

    /* ******************* */
    /*  TEST / UI Helpers  */
    /* ******************* */

    /// @notice Get the amount of coin j one would have to pay as trading fees for swapping dx of coin i
    /// @param cryptoSwap Curve Cryptoswap contract
    /// @param i Index of the coin to sell
    /// @param j Index of the coin to buy
    /// @param dx Amount to sell
    /// @return Amount of token j payed as trading fees. 18 decimals
    function get_dy_fees(
        ICryptoSwap cryptoSwap,
        uint256 i,
        uint256 j,
        uint256 dx
    ) external view override returns (uint256) {
        uint256 dy_ex_fees = get_dy_ex_fees(cryptoSwap, i, j, dx);
        uint256 dy = cryptoSwap.get_dy(i, j, dx);
        return dy_ex_fees - dy;
    }

    /// @notice Get the share of coin j one would have to pay as trading fees for swapping dx of coin i
    /// @param cryptoSwap Curve Cryptoswap contract
    /// @param i Index of the coin to sell
    /// @param j Index of the coin to buy
    /// @param dx Amount to sell
    /// @return Share of token j payed as trading fees. 18 decimals
    function get_dy_fees_perc(
        ICryptoSwap cryptoSwap,
        uint256 i,
        uint256 j,
        uint256 dx
    ) external view override returns (uint256) {
        uint256 dy_ex_fees = get_dy_ex_fees(cryptoSwap, i, j, dx);
        uint256 dy = cryptoSwap.get_dy(i, j, dx);
        uint256 feesPayed = dy_ex_fees - dy;
        return feesPayed.wadDiv(dy_ex_fees);
    }

    /// @notice Get the amount of coin i one would have to pay for receiving dy of coin j (before any trading fees are charged)
    /// @dev Does not get the identical estimate (i.e. get_dy(get_dx(100))) ~= 100)
    ///      Question: Why is that the case? Answer: Newton_y stops when the converge limit is reached:
    ///      https://github.com/curvefi/curve-crypto-contract/blob/d7d04cd9ae038970e40be850df99de8c1ff7241b/contracts/two/CurveCryptoSwap2.vy#L355
    /// @dev Should be used for external contracts to get a fairly precise "estimate" of the amount of tokens one has to pay to receive dy tokens
    /// @param cryptoSwap Curve Cryptoswap contract
    /// @param i Index of the coin to sell
    /// @param j Index of the coin to buy
    /// @param dy Amount to tokens to receive
    /// @return Amount of tokens to sell
    function get_dx_ex_fees(
        ICryptoSwap cryptoSwap,
        uint256 i,
        uint256 j,
        uint256 dy
    ) external view override returns (uint256) {
        require(i != j && i < N_COINS, "coin index out of range");
        require(dy > 0, "do not exchange 0 coins");

        uint256 price_scale = cryptoSwap.price_scale();
        uint256[2] memory xp = [cryptoSwap.balances(0), cryptoSwap.balances(1)];

        uint256 A = cryptoSwap.A();
        uint256 gamma = cryptoSwap.gamma();
        uint256 D = cryptoSwap.D();

        if (cryptoSwap.future_A_gamma_time() > 0) {
            D = math.newton_D(A, gamma, [xp[0] * PRECISIONS[0], (xp[1] * PRECISIONS[1] * price_scale) / PRECISION]);
        }

        xp[j] -= dy;
        xp = [xp[0] * PRECISIONS[0], (xp[1] * price_scale) / PRECISION];

        uint256 x = math.newton_y(A, gamma, xp, D, i);
        uint256 dx = x - xp[i] + 1;
        xp[i] = x;
        if (i > 0) {
            dx = (dx * PRECISION) / price_scale;
        } else {
            dx /= PRECISIONS[0];
        }

        return dx;
    }
}

